import React, {useState, useEffect} from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
import {
  createBrowserRouter,
  RouterProvider,
} from "react-router-dom";
import User_Home from './Pages/User_Home'
import Login from './Pages/Login'
import Register from './Pages/Register'
import Admin_Home from './Pages/Admin_Home';
import { fetch_token } from './fetchtoken';
import axios from 'axios';
import swal from 'sweetalert';
const token = fetch_token();



const router = createBrowserRouter([
  {
    path: "/",
    element: <App/>,
  },
  {
    path: "/home",
    element: user.admin?<Admin_Home/>:<User_Home/>,
  },
  {
    path: "/login",
    element: <Login/>,
  },
  {
    path: "/register",
    element: <Register/>,
  },

]);

const root = ReactDOM.createRoot(document.getElementById('root'));
const [user, setuser] = useState({})

useEffect(() => {
  const get_data = async () => {
    try {
      const data = await axios.get(`http://localhost:5000/auth/${token}`);
      setuser(data.data);
    } catch (error) {
      swal("Login again", `${error.message}`);
    }
  };
  get_data();
}, []);
root.render(
  <React.StrictMode>
    <RouterProvider router={router}/>
  </React.StrictMode>
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
